new_list = re.split(r"(?<=[0-9IVXLCDM-])[\.,](?=[A-Z][^IVXLCDM\.,])", starting_string) #řádky mají být oddělené tečkou nebo čárkou, končí číslem (i římským) a začínají slovem
name_pat = r"^[A-Z][\w\s'-]+\.?[\w\s'-]+[a-zà-ž](?=(\s{12}|\.))" #jméno je první na každém řádku, za ním tečka nebo 12 mezer, Slobodan Mološevič dělal bordel
uni_pat = r"(?<=\s{12})[A-Z][\w\s']+[a-z]" #nejtěžší na pohlídání. Když zklame tento regex, je uni chytaná ještě dále
state_pat = r"(?<=,\s)[A-Z][\w\s]+[a-z](?=,)" #před státem je čárka a whitespace,za ním čárka
num_data_pat = r"[\dIVXLCDM]+,[\dIVXLCDM]+,([\dIVXLCDM]+\.[\dIVXLCDM]{2}|-)" #římská čísla nebo "-" taky hodnoty
list_of_dicts = []
for line in new_list:
    match_name = re.search(name_pat, line)
    match_uni = re.search(uni_pat, line)
    match_state = re.search(state_pat, line)
    match_num_data = re.search(num_data_pat, line)
    pub = "0"
    cit = "0"
    ratio = "-"
    if match_num_data:
        pub, cit, ratio = match_num_data.group().split(",")
    dict = {"name":"", "uni":"", "state":"", "publications":0, "citations":0, "ratio":"-"}
    if match_name:
        dict["name"] = match_name.group()
    else:
        uni_pat_s = r"^\w[\w\s]+[a-z](?=,)" #jméno není, regex školy měníme na začátek
        match_uni = re.search(uni_pat_s, line)
    if not match_uni:
        uni_pat_t = r"(?<=[a-z]\.)\w[\w\s]+[a-z](?=,)" #special cases kvůli nepravidelnému formátování
        match_uni = re.search(uni_pat_t, line)
    if not match_uni:
        uni_pat_f = r"B\.I\. Stepanov [\w\s]*(?=,)" #Ty tečky do názvu školy někdo dal za trest, se omlouvám, na to nemám
        match_uni = re.search(uni_pat_f, line)
    if match_uni:
        dict["uni"] = match_uni.group()
    if match_state:
        dict["state"] = match_state.group()
    dict["publications"] = pub
    dict["citations"] = cit
    dict["ratio"] = ratio
    list_of_dicts.append(dict)
data_fr = pd.DataFrame(list_of_dicts)
data_fr.to_excel("data.xlsx")
